export default function HomePage() {
  const date = new Date()
  const hours = date.getHours()
  let timeOfDay
  const styles = {}

  if (hours < 12) {
    timeOfDay = "morning"
    styles.color = "orange"
  } else if (hours >= 12 && hours < 17) {
    timeOfDay = "afternoon" 
    styles.color = "red"
  } else {
    timeOfDay = "night"
    styles.color = "green"
  }

  return (
    <h1 style = {styles} >Good {timeOfDay}!</h1>
  )
}
